abilities:\n                # Host container properties\n                host:\n                    properties:\n                       num_cpus: 2\n                       disk_size: 10GB\n                       mem_size: 512MB\n                # Guest Operating System properties\n                os:\n                    properties:\n                        architecture: x86_64\n                        type: sfc_client\n                        distribution: ubuntu\n                        version: 14.04\n        \n        portT-9B-2:\n            type: tosca.nodes.network.Port\n            requirements:\n                - binding:\n                    node: VM-9\n                - link:\n                    node: NET-2\n\n        portT-7B-2:\n            type: tosca.nodes.network.Port\n            requirements:\n                - binding:\n                    node: VM-7\n                - link:\n                    node: NET-2\n\n        portT-10B-2:\n            type: tosca.nodes.network.Port\n            requirements:\n                - binding:\n                    node: VM-10\n                - link:\n                    node: NET-2\n        VNF1:\n              type: tosca.nodes.nfv.VNF\n              properties:\n              attributes:\n                    type: dpi\n                    address:\n                    port: 40000\n                    nsh_aware: true\n              requirements:\n                    - host: VM-9\n        CP11:          #endpoints of VNF1 linked to VL1\n              type: tosca.nodes.nfv.CP\n              properties:\n              attributes:\n                    IP_address:\n                    interface: ens3\n                    port: 30000\n              requirements:\n                    - virtualBinding: VNF1\n                    - virtualLink: VL1\n        VNF2:\n              type: tosca.nodes.nfv.VNF\n              properties:\n              attributes:\n                    type: firewall\n                    address